import React, { useEffect, useState } from 'react';
import './App.css';

const LS_KEY = 'campusData_v1';

function loadStored() {
	try { return JSON.parse(localStorage.getItem(LS_KEY)) || {}; } catch { return {}; }
}

async function fetchJson(path) {
	const res = await fetch(path);
	if (!res.ok) throw new Error('Failed to load ' + path);
	return res.json();
}

export default function App() {
	const [menuCycle, setMenuCycle] = useState(null);
	const [busSchedule, setBusSchedule] = useState(null);
	const [loading, setLoading] = useState(true);
	const [error, setError] = useState(null);

	// initial load
	useEffect(() => {
		const stored = loadStored();
		if (stored.menuCycle) setMenuCycle(stored.menuCycle);
		if (stored.busSchedule) setBusSchedule(stored.busSchedule);
		Promise.all([
			!stored.menuCycle && fetchJson('/data/menuCycle.json').catch(e => ({ __err: e.message })),
			!stored.busSchedule && fetchJson('/data/busSchedule.json').catch(e => ({ __err: e.message }))
		]).then(([mc, bs]) => {
			if (mc && !mc.__err && !stored.menuCycle) setMenuCycle(mc);
			if (bs && !bs.__err && !stored.busSchedule) setBusSchedule(bs);
		}).catch(e => setError(e.message)).finally(() => setLoading(false));
	}, []);

	// persist
	useEffect(() => {
		const data = { menuCycle, busSchedule };
		localStorage.setItem(LS_KEY, JSON.stringify(data));
	}, [menuCycle, busSchedule]);

	if (loading) return <div className="app-shell"><h2>Loading...</h2></div>;
	if (error) return <div className="app-shell error">Error: {error}</div>;

	return (
		<div className="app-shell">
			<h1>Campus Dashboard (Static)</h1>
			<section>
				<h2>Mess Menu (raw)</h2>
				<pre className="scroll-box">{JSON.stringify(menuCycle, null, 2)}</pre>
			</section>
			<section>
				<h2>Bus Schedule (raw)</h2>
				<pre className="scroll-box">{JSON.stringify(busSchedule, null, 2)}</pre>
			</section>
			<p className="note">This static build stores edits only in your browser (localStorage). To reset, clear site data.</p>
		</div>
	);
}
