import React, { useEffect, useMemo, useState } from 'react';
import './App.css';
import MessMenu from './components/MessMenu';
import CampusBus from './components/CampusBus';
import SimpleDataPanel from './components/SimpleDataPanel';
import { loadData, saveData } from './utils/storage';

const DEFAULT_DATA = {
  menu: {
    breakfast: 'Fried Idly, Vada, Sambar, Coconut Chutney, Omelette (opt)',
    lunch: 'Phulka, White Rice, Kadai Veg, Sambar, Potato-Cabbage Dry, Buttermilk',
    snacks: 'Pungulu with Coconut Chutney',
    dinner: 'Chicken Gravy / Paneer Butter Masala, Pulao, Mix Dal, Chapathi, Jalebi',
  },
  // 4-week repeating cycle. weeks 1&3 same, weeks 2&4 same
  menuCycle: {
    startDate: null, // ISO date string when cycle started; if null, legacy menu used
    type: '4-week', // '14-day' or '4-week'
    weeks: {
      // week13: { common: {breakfast,lunch,snacks,dinner}, days: {monday:{breakfast,lunch,snacks,dinner}, ...} }
      // week24: { common: {breakfast,lunch,snacks,dinner}, days: {monday:{breakfast,lunch,snacks,dinner}, ...} }
    }
  },
  buses: {
    routes: [
      { route: 'Nila → Sahyadri', times: ['9:00 PM', '10:00 PM', '11:00 PM', '12:00 AM'] },
      { route: 'Sahyadri → Nila', times: ['9:15 PM', '10:15 PM', '11:15 PM', '7:30 AM'] },
    ],
    // 7-day weekly bus schedule; index 0=Monday ... 6=Sunday
    week: {
      startDate: null, // Monday of the current schedule week; if null, fallback to routes
      days: [] // each element: { routes: { 'Nila → Sahyadri':[], 'Sahyadri → Nila':[] } }
    }
  },
};

function App() {
  const [data, setData] = useState(() => loadData() || DEFAULT_DATA);
  const [isImportOpen, setImportOpen] = useState(false);
  const [notification, setNotification] = useState(null);

  useEffect(() => {
    saveData(data);
  }, [data]);

  const today = useMemo(() => new Date(), []);
  const dateOptions = { weekday: 'short', day: '2-digit', month: 'short', year: 'numeric' };
  const formattedDate = today.toLocaleDateString('en-US', dateOptions);

  // Derive today's menu from 14-day cycle if available
  const todayMenu = useMemo(() => {
    const cycle = data.menuCycle;
    if (!cycle || !cycle.startDate || !Array.isArray(cycle.days) || cycle.days.length !== 14) return data.menu;
    try {
      const start = new Date(cycle.startDate + 'T00:00:00');
      const diffDays = Math.floor((today - start) / 86400000);
      if (diffDays < 0) return data.menu;
      const idx = diffDays % 14;
      return { ...cycle.days[idx] };
    } catch { return data.menu; }
  }, [data.menuCycle, data.menu, today]);

  const menuCycleMeta = useMemo(() => {
    const cycle = data.menuCycle;
    if (!cycle || !cycle.startDate || !Array.isArray(cycle.days) || cycle.days.length !== 14) return null;
    const start = new Date(cycle.startDate + 'T00:00:00');
    const diffDays = Math.floor((today - start) / 86400000);
    if (diffDays < 0) return null;
    return { dayIndex: diffDays % 14, length: 14 };
  }, [data.menuCycle, today]);

  // Derive today's bus schedule from week (7-day) if available
  const todayBusRoutes = useMemo(() => {
    const week = data.buses.week;
    if (!week || !week.startDate || !Array.isArray(week.days) || week.days.length !== 7) return data.buses.routes;
    try {
      const start = new Date(week.startDate + 'T00:00:00');
      const diffDays = Math.floor((today - start) / 86400000);
      if (diffDays < 0) return data.buses.routes;
      const idx = diffDays % 7; // 0..6
      const dayObj = week.days[idx];
      if (!dayObj || !dayObj.routes) return data.buses.routes;
      return [
        { route: 'Nila → Sahyadri', times: dayObj.routes['Nila → Sahyadri'] || [] },
        { route: 'Sahyadri → Nila', times: dayObj.routes['Sahyadri → Nila'] || [] }
      ];
    } catch { return data.buses.routes; }
  }, [data.buses.week, data.buses.routes, today]);

  return (
    <div className="App">
      {notification && (
        <div className="notification">
          {notification}
        </div>
      )}

      <header className="app-header">
        <div className="header-row">
          <h1>Today ({formattedDate})</h1>
          <div className="header-actions">
            <button className="ghost-btn" onClick={() => setData(DEFAULT_DATA)} title="Restore defaults">Reset</button>
            <button className="update-btn" onClick={() => setImportOpen(true)}>Update Data</button>
          </div>
        </div>
        <div className="day-tabs">
          <button className="day-tab active">Today</button>
          <button className="day-tab" title="Coming soon">Tomorrow</button>
        </div>
      </header>

      <div className="main-content">
  <MessMenu menu={todayMenu} cycleMeta={menuCycleMeta} />
        <CampusBus 
          routes={todayBusRoutes}
          multi={data.buses.multi} 
          specials={data.buses.specials}
        />
      </div>

      {isImportOpen && (
        <SimpleDataPanel
          onClose={() => setImportOpen(false)}
          onSave={(newData) => {
            setData(newData);
            setImportOpen(false);
            setNotification('Data updated successfully!');
            setTimeout(() => setNotification(null), 3000);
          }}
          currentData={data}
        />
      )}
    </div>
  );
}

export default App;
