import React, { useMemo } from 'react';

const CATEGORY_LABELS = {
  working: 'Working Days',
  saturday: 'Sat / MF Holidays',
  sunday: 'Sundays'
};

function highlightDuplicates(times) {
  const counts = times.reduce((acc, t) => { acc[t] = (acc[t]||0)+1; return acc; }, {});
  return times.map(t => ({ value: t, dup: counts[t] > 1 }));
}

const CampusBus = ({ routes = [], multi, specials }) => {
  const hasMulti = !!multi;

  const { todayRoutes, categoryLabel } = useMemo(() => {
    if (!hasMulti) {
      return { todayRoutes: routes, categoryLabel: 'Bus' };
    }
    const day = new Date().getDay();
    let key = 'working';
    if (day === 0) key = 'sunday';
    else if (day === 6) key = 'saturday';
    const cat = multi[key];
    if (!cat) return { todayRoutes: routes, categoryLabel: 'Bus' };
    return {
      todayRoutes: [
        { route: 'Nila → Sahyadri', times: cat.routes['Nila → Sahyadri'] || [] },
        { route: 'Sahyadri → Nila', times: cat.routes['Sahyadri → Nila'] || [] }
      ],
      categoryLabel: CATEGORY_LABELS[key] || 'Bus'
    };
  }, [multi, hasMulti, routes]);

  return (
    <div className="campus-bus">
      <h2>Campus Bus</h2>

      {hasMulti && (
        <div className="today-bus-note">Showing: {categoryLabel}</div>
      )}

      {todayRoutes.map((schedule, idx) => (
        <div key={idx} className="bus-route">
          <h3 className="route-title">{schedule.route}</h3>
          <div className="time-slots">
            {highlightDuplicates(schedule.times).map((tObj, index) => (
              <button
                key={index}
                className={`time-slot ${tObj.dup ? 'dup' : ''}`}
                title={tObj.dup ? 'Multiple buses at this time' : ''}
              >
                {tObj.value}
              </button>
            ))}
          </div>
        </div>
      ))}

      {specials && (specials.palakkadTown?.length || specials.wisePark?.length) && (
        <div className="special-routes">
          {specials.palakkadTown?.length > 0 && (
            <div className="special-block">
              <h4>Palakkad Town</h4>
              <ul>
                {specials.palakkadTown.map((s, i) => <li key={i}>{s}</li>)}
              </ul>
            </div>
          )}
          {specials.wisePark?.length > 0 && (
            <div className="special-block">
              <h4>Wise Park Junction</h4>
              <ul>
                {specials.wisePark.map((s, i) => <li key={i}>{s}</li>)}
              </ul>
            </div>
          )}
        </div>
      )}

  {/* Simple version: no modal/button as requested */}
    </div>
  );
};

export default CampusBus;
