import React from 'react';
import './App.css';
import MessMenu from './components/MessMenu';
import CampusBus from './components/CampusBus';

function App() {
  const today = new Date();
  const dateOptions = { 
    weekday: 'short', 
    day: '2-digit', 
    month: 'short', 
    year: 'numeric' 
  };
  const formattedDate = today.toLocaleDateString('en-US', dateOptions);

  return (
    <div className="App">
      <header className="app-header">
        <h1>Today ({formattedDate})</h1>
        <div className="day-tabs">
          <button className="day-tab active">Today</button>
          <button className="day-tab">Tomorrow</button>
        </div>
      </header>

      <div className="main-content">
        <MessMenu />
        <CampusBus />
      </div>
    </div>
  );
}

export default App;
