import React, { useMemo, useRef, useState } from 'react';
import { extractTextFromPdf } from '../utils/pdf';
import { parseAllFromText } from '../utils/parser';

const DataImportPanel = ({ onClose, onSave, initialText = '', currentData }) => {
  const [rawText, setRawText] = useState(initialText);
  const [error, setError] = useState('');
  const [busy, setBusy] = useState(false);
  const fileRef = useRef();

  const parsedPreview = useMemo(() => {
    try {
      return parseAllFromText(rawText, currentData);
    } catch (e) {
      return null;
    }
  }, [rawText, currentData]);

  const onPickPdf = async (e) => {
    const file = e.target.files && e.target.files[0];
    if (!file) return;
    setBusy(true);
    setError('');
    try {
      const text = await extractTextFromPdf(file);
      setRawText(text);
    } catch (err) {
      setError('Failed to read PDF. Try copying text instead.');
    } finally {
      setBusy(false);
    }
  };

  const handleSave = () => {
    try {
      const data = parseAllFromText(rawText, currentData);
      onSave(data);
    } catch (err) {
      setError('Could not parse the provided text. Please check the format.');
    }
  };

  return (
    <div className="modal-backdrop" role="dialog" aria-modal="true">
      <div className="modal">
        <div className="modal-header">
          <h3>Update Data</h3>
          <button className="icon-btn" onClick={onClose} aria-label="Close">✕</button>
        </div>

        <div className="import-actions">
          <button className="secondary-btn" onClick={() => fileRef.current?.click()} disabled={busy}>
            Upload PDF
          </button>
          <input ref={fileRef} type="file" accept="application/pdf" onChange={onPickPdf} style={{ display: 'none' }} />
        </div>

        <label className="field-label">Paste text (menu + buses) or JSON:</label>
        <textarea
          className="text-input"
          rows={10}
          placeholder={
            'Example:\nBreakfast: Idly, Vada, Sambar\nLunch: Rice, Sambar, ...\nSnacks: ...\nDinner: ...\n\nBuses:\nNila → Sahyadri: 9:00 PM, 10:00 PM\nSahyadri → Nila: 9:15 PM, 10:15 PM'
          }
          value={rawText}
          onChange={(e) => setRawText(e.target.value)}
        />

        {error && <div className="error-text">{error}</div>}

        <div className="preview">
          <h4>Preview</h4>
          <pre className="preview-json">{JSON.stringify(parsedPreview || currentData, null, 2)}</pre>
        </div>

        <div className="modal-footer">
          <button className="secondary-btn" onClick={onClose}>Cancel</button>
          <button className="primary-btn" onClick={handleSave} disabled={busy}>Save</button>
        </div>
      </div>
    </div>
  );
};

export default DataImportPanel;
