import React, { useEffect, useMemo, useState } from 'react';
import './App.css';
import MessMenu from './components/MessMenu';
import CampusBus from './components/CampusBus';
import SimpleDataPanel from './components/SimpleDataPanel';
import { loadData, saveData } from './utils/storage';

const DEFAULT_DATA = {
  menu: {
    breakfast: 'Fried Idly, Vada, Sambar, Coconut Chutney, Omelette (opt)',
    lunch: 'Phulka, White Rice, Kadai Veg, Sambar, Potato-Cabbage Dry, Buttermilk',
    snacks: 'Pungulu with Coconut Chutney',
    dinner: 'Chicken Gravy / Paneer Butter Masala, Pulao, Mix Dal, Chapathi, Jalebi',
  },
  // 14-day repeating cycle. day0..day13 each: { breakfast,lunch,snacks,dinner }
  menuCycle: {
    startDate: null, // ISO date string when cycle started; if null, legacy menu used
    days: [] // to be filled by import (length 14)
  },
  buses: {
    routes: [
      { route: 'Nila → Sahyadri', times: ['9:00 PM', '10:00 PM', '11:00 PM', '12:00 AM'] },
      { route: 'Sahyadri → Nila', times: ['9:15 PM', '10:15 PM', '11:15 PM', '7:30 AM'] },
    ],
    // 7-day weekly bus schedule; index 0=Monday ... 6=Sunday
    week: {
      startDate: null, // Monday of the current schedule week; if null, fallback to routes
      days: [] // each element: { routes: { 'Nila → Sahyadri':[], 'Sahyadri → Nila':[] } }
    }
  },
};

function App() {
  const [data, setData] = useState(() => loadData() || DEFAULT_DATA);
  const [isImportOpen, setImportOpen] = useState(false);
  const [notification, setNotification] = useState(null);

  useEffect(() => {
    saveData(data);
  }, [data]);

  const today = useMemo(() => new Date(), []);
  const dateOptions = { weekday: 'short', day: '2-digit', month: 'short', year: 'numeric' };
  const formattedDate = today.toLocaleDateString('en-US', dateOptions);

  return (
    <div className="App">
      {notification && (
        <div className="notification">
          {notification}
        </div>
      )}

      <header className="app-header">
        <div className="header-row">
          <h1>Today ({formattedDate})</h1>
          <div className="header-actions">
            <button className="ghost-btn" onClick={() => setData(DEFAULT_DATA)} title="Restore defaults">Reset</button>
            <button className="update-btn" onClick={() => setImportOpen(true)}>Update Data</button>
          </div>
        </div>
        <div className="day-tabs">
          <button className="day-tab active">Today</button>
          <button className="day-tab" title="Coming soon">Tomorrow</button>
        </div>
      </header>

      <div className="main-content">
        <MessMenu menu={data.menu} />
        <CampusBus 
          routes={data.buses.routes} 
          multi={data.buses.multi} 
          specials={data.buses.specials}
        />
      </div>

      {isImportOpen && (
        <SimpleDataPanel
          onClose={() => setImportOpen(false)}
          onSave={(newData) => {
            setData(newData);
            setImportOpen(false);
            setNotification('Data updated successfully!');
            setTimeout(() => setNotification(null), 3000);
          }}
          currentData={data}
        />
      )}
    </div>
  );
}

export default App;
