import React, { useEffect, useMemo, useState } from 'react';
import './App.css';
import MessMenu from './components/MessMenu';
import CampusBus from './components/CampusBus';
import DataImportPanel from './components/DataImportPanel';
import EmailSettingsPanel from './components/EmailSettingsPanel';
import { loadData, saveData, loadEmailSettings } from './utils/storage';
import EmailService from './utils/emailService';
import { parseAllFromText } from './utils/parser';

const DEFAULT_DATA = {
  menu: {
    breakfast: 'Fried Idly, Vada, Sambar, Coconut Chutney, Omelette (opt)',
    lunch: 'Phulka, White Rice, Kadai Veg, Sambar, Potato-Cabbage Dry, Buttermilk',
    snacks: 'Pungulu with Coconut Chutney',
    dinner: 'Chicken Gravy / Paneer Butter Masala, Pulao, Mix Dal, Chapathi, Jalebi',
  },
  buses: {
    routes: [
      { route: 'Nila → Sahyadri', times: ['9:00 PM', '10:00 PM', '11:00 PM', '12:00 AM'] },
      { route: 'Sahyadri → Nila', times: ['9:15 PM', '10:15 PM', '11:15 PM', '7:30 AM'] },
    ],
  },
};

function App() {
  const [data, setData] = useState(() => loadData() || DEFAULT_DATA);
  const [isImportOpen, setImportOpen] = useState(false);
  const [isEmailSettingsOpen, setEmailSettingsOpen] = useState(false);
  const [emailSettings, setEmailSettings] = useState(() => loadEmailSettings());
  const [lastEmailCheck, setLastEmailCheck] = useState(null);
  const [notification, setNotification] = useState(null);

  useEffect(() => {
    saveData(data);
  }, [data]);

  // Auto-check emails if enabled
  useEffect(() => {
    if (!emailSettings?.enabled) return;

    const checkEmails = async () => {
      try {
        await EmailService.loadRequiredScripts(emailSettings.provider);
        const emailService = new EmailService(emailSettings);

        if (!emailService.isAuthorized) {
          const authorized = await emailService.authorize();
          if (!authorized) return;
        }

        const emails = await emailService.fetchLatestEmails();

        // Process the most recent email
        if (emails.length > 0) {
          const latestEmail = emails[0];
          if (!lastEmailCheck || latestEmail.date > lastEmailCheck) {
            const parsedData = parseAllFromText(latestEmail.content, data);
            if (parsedData) {
              setData(parsedData);
              setLastEmailCheck(latestEmail.date);
              setNotification('Dashboard updated from email!');
              setTimeout(() => setNotification(null), 5000);
            }
          }
        }
      } catch (error) {
        console.error('Email check failed:', error);
      }
    };

    // Check immediately and then set interval
    checkEmails();
    const interval = setInterval(checkEmails, emailSettings.refreshInterval * 60 * 1000);

    return () => clearInterval(interval);
  }, [emailSettings, lastEmailCheck, data]);

  const today = useMemo(() => new Date(), []);
  const dateOptions = { weekday: 'short', day: '2-digit', month: 'short', year: 'numeric' };
  const formattedDate = today.toLocaleDateString('en-US', dateOptions);

  return (
    <div className="App">
      <header className="app-header">
        <div className="header-row">
          <h1>Today ({formattedDate})</h1>
          <div className="header-actions">
            <button className="ghost-btn" onClick={() => setData(DEFAULT_DATA)} title="Restore defaults">Reset</button>
            <button className="update-btn" onClick={() => setImportOpen(true)}>Update Data</button>
          </div>
        </div>
        <div className="day-tabs">
          <button className="day-tab active">Today</button>
          <button className="day-tab" title="Coming soon">Tomorrow</button>
        </div>
      </header>

      <div className="main-content">
        <MessMenu menu={data.menu} />
        <CampusBus routes={data.buses.routes} />
      </div>

      {isImportOpen && (
        <DataImportPanel
          initialText=""
          onClose={() => setImportOpen(false)}
          onSave={(newData) => {
            setData(newData);
            setImportOpen(false);
          }}
          currentData={data}
        />
      )}
    </div>
  );
}

export default App;
