import React, { useEffect, useMemo, useState } from 'react';

const CATEGORY_LABELS = {
  working: 'Working Days',
  saturday: 'Sat / MF Holidays',
  sunday: 'Sundays'
};

function highlightDuplicates(times) {
  const counts = times.reduce((acc, t) => { acc[t] = (acc[t]||0)+1; return acc; }, {});
  return times.map(t => ({ value: t, dup: counts[t] > 1 }));
}

function parseToMinutes(t) {
  // Accept formats HH:MM or H:MM 24h; if contains AM/PM skip conversion (not used here)
  const m = t.match(/^(\d{1,2}):(\d{2})$/);
  if (!m) return null;
  const h = parseInt(m[1],10);
  const min = parseInt(m[2],10);
  return h*60+min;
}

function getCurrentTimeMinutes() {
  const now = new Date();
  return now.getHours() * 60 + now.getMinutes();
}

const CampusBus = ({ routes = [], multi, specials, hidePastTimes, holidayOverride = 'auto', showCountdown, dayOffset = 0 }) => {
  const hasMulti = !!multi;
  const [showSpecials, setShowSpecials] = useState(false);
  const [now, setNow] = useState(new Date());
  useEffect(() => {
    if (!showCountdown) return; // only tick when countdown on
    const id = setInterval(() => setNow(new Date()), 30000); // 30s updates are enough
    return () => clearInterval(id);
  }, [showCountdown]);

  const { todayRoutes, categoryLabel } = useMemo(() => {
    if (!hasMulti) {
      return { todayRoutes: routes, categoryLabel: 'Bus' };
    }
  const baseDate = new Date();
  const baseDay = new Date(baseDate.getFullYear(), baseDate.getMonth(), baseDate.getDate() + dayOffset).getDay();
    let key = 'working';
    const effectiveDay = holidayOverride === 'auto' ? baseDay : (holidayOverride === 'sunday' ? 0 : holidayOverride === 'saturday' ? 6 : 3); // 3 => Wed working sample
    if (effectiveDay === 0) key = 'sunday';
    else if (effectiveDay === 6) key = 'saturday';
    const cat = multi[key];
    if (!cat) return { todayRoutes: routes, categoryLabel: 'Bus' };
    return {
      todayRoutes: [
        { route: 'Nila → Sahyadri', times: cat.routes['Nila → Sahyadri'] || [] },
        { route: 'Sahyadri → Nila', times: cat.routes['Sahyadri → Nila'] || [] }
      ],
      categoryLabel: CATEGORY_LABELS[key] || 'Bus'
    };
  }, [multi, hasMulti, routes, holidayOverride]);

  // Next bus detection (combine both directions, pick earliest future)
  const nowMin = getCurrentTimeMinutes();
  const allTimes = todayRoutes.flatMap(r => r.times.map(tm => ({ route: r.route, time: tm, minutes: parseToMinutes(tm) })));
  const future = allTimes.filter(x => x.minutes != null && x.minutes > nowMin).sort((a,b)=>a.minutes-b.minutes);
  const next = future[0];

  const countdown = useMemo(() => {
    if (!showCountdown || !next) return null;
    const mins = next.minutes - nowMin;
    if (mins <= 0) return null;
    if (mins < 60) return mins + ' min';
    const h = Math.floor(mins/60);
    const m = mins % 60;
    return h + 'h ' + (m? m + 'm':'');
  }, [next, nowMin, showCountdown, now]);

  // Enhanced time rendering with next highlight and past dimming
  const renderTimeSlots = (times, routeName) => {
    return highlightDuplicates(times).map((tObj, index) => {
      const timeMin = parseToMinutes(tObj.value);
      const isPast = timeMin != null && timeMin <= nowMin;
      const isNext = next && next.time === tObj.value && next.route === routeName;

  if (hidePastTimes && isPast && !isNext) return null;
  return (
        <button
          key={index}
          className={`time-slot ${tObj.dup ? 'dup' : ''} ${isPast ? 'past' : ''} ${isNext ? 'next' : ''}`}
          title={tObj.dup ? 'Multiple buses at this time' : isNext ? 'Next bus' : isPast ? 'Time passed' : ''}
        >
          {tObj.value}
        </button>
      );
    });
  };

  return (
    <div className="campus-bus">
      <h2 className="bus-header-row">
        <span>Campus Bus</span>
        {next && (
          <span className="next-bus-badge" title={next.route}>
            Next: {next.time}{countdown ? ` (${countdown})` : ''}
          </span>
        )}
      </h2>

      {hasMulti && (
        <div className="today-bus-note">Showing: {categoryLabel}</div>
      )}

      {todayRoutes.map((schedule, idx) => (
        <div key={idx} className="bus-route">
          <h3 className="route-title">{schedule.route}</h3>
          <div className="time-slots">
            {renderTimeSlots(schedule.times, schedule.route)}
          </div>
        </div>
      ))}

      {specials && (specials.palakkadTown?.length || specials.wisePark?.length) && (
        <div className="specials-wrapper">
          <button className="toggle-specials-btn" onClick={() => setShowSpecials(s => !s)}>
            {showSpecials ? 'Hide' : 'Show'} Special Routes
          </button>
          {showSpecials && (
            <div className="special-routes">
              {specials.palakkadTown?.length > 0 && (
                <div className="special-block">
                  <h4>Palakkad Town</h4>
                  <ul>
                    {specials.palakkadTown.map((s, i) => <li key={i}>{s}</li>)}
                  </ul>
                </div>
              )}
              {specials.wisePark?.length > 0 && (
                <div className="special-block">
                  <h4>Wise Park Junction</h4>
                  <ul>
                    {specials.wisePark.map((s, i) => <li key={i}>{s}</li>)}
                  </ul>
                </div>
              )}
            </div>
          )}
        </div>
      )}

  {/* Simple version: no modal/button as requested */}
    </div>
  );
};

export default CampusBus;
